	function [RS] = REFL_S(r11,v11,r12,v12,r21,v21,r22,v22,RL)	%	%[R2] = REFL_S(r11,v11,r12,v12,r21,v21,r22,v22,RL)	%Litt. 7 side 571 eq. 12.38a	%	%INPUT:	%r11: abs. S11, v11: vinkel af S11 i grader, etc.	%RL: Belastningsimpedansens reflektionkooeficient	%	%OUTPUT:	%RS: Kildeimpedansens reflektionkooeficient	%	%sl@tek.sdu.dk	%	s11=r11*cos(v11/180*pi)+j*r11*sin(v11/180*pi);	s12=r12*cos(v12/180*pi)+j*r12*sin(v12/180*pi);	s21=r21*cos(v21/180*pi)+j*r21*sin(v21/180*pi);	s22=r22*cos(v22/180*pi)+j*r22*sin(v22/180*pi);	RS=s11+(s12*s21*RL)/(1-s22*RL);	ZSn=(1+RS)/(1-RS);	RS_rekt=sprintf(num2str(RS))		RS_abs_vaerdi=sprintf(num2str(abs(RS)))	RS_vinkel_i_grader=sprintf(num2str(angle(RS)/pi*180))	ZSn_rekt=sprintf(num2str(ZSn))