	function timedelay(num,den,w1,w2)	%	%	num: taeller pol. eks. [0 0 0 1]	%	den: naevner pol. eks. [1 2 2 1]	%	w1: nedre vinkelfrekvens i rad/s.	%	w2: Ã¸vre vinkelfrekvens i rad/s.	%	ver 1. uden garanti (SL)	%	w = logspace(log10(w1),log10(w2),1000);	h = freqs(num,den,w);	mag = abs(h);	phase = angle(h);	phasedeg = phase*180/pi;	delay = -unwrap(phase)./w;	subplot(2,1,1), loglog(w,mag), grid on	xlabel 'Frequency (rad/s)', ylabel Magnitude	subplot(2,1,2), semilogx(w,delay), grid on	xlabel 'Frequency (rad/s)', ylabel 'Delay (sec.)'