	function [RL] = REFL_L(r11,v11,r12,v12,r21,v21,r22,v22,RS)	%	%[RL] = REFL_L(r11,v11,r12,v12,r21,v21,r22,v22,RS)	%Litt. 7 side 571 eq. 12.38b	%	%INPUT:	%r11: abs. S11, v11: vinkel af S11 i grader, etc.	%RS: Kildeimpedansens reflektionkooeficient	%	%OUTPUT:	%RL: Belastningsimpedansens reflektionkooeficient	%	%sl@tek.sdu.dk	%	s11=r11*cos(v11/180*pi)+j*r11*sin(v11/180*pi);	s12=r12*cos(v12/180*pi)+j*r12*sin(v12/180*pi);	s21=r21*cos(v21/180*pi)+j*r21*sin(v21/180*pi);	s22=r22*cos(v22/180*pi)+j*r22*sin(v22/180*pi);	RL=s22+(s12*s21*RS)/(1-s11*RS);	ZLn=(1+RL)/(1-RL);	RL_rekt=sprintf(num2str(RL))		RL_abs_vaerdi=sprintf(num2str(abs(RL)))	RL_vinkel_i_grader=sprintf(num2str(angle(RL)/pi*180))	ZLn_rekt=sprintf(num2str(ZLn))	